import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);

  useEffect(() => {
    fetch('/employeeData.json')
      .then(response => response.json())
      .then(data => setEmployees(data.data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  const handleEdit = (employee) => {
    setSelectedEmployee(employee);
  };

  const handleSave = (updatedEmployee) => {
    setEmployees(prevEmployees =>
      prevEmployees.map(emp =>
        emp.id === updatedEmployee.id ? updatedEmployee : emp
      )
    );

    setSelectedEmployee(null);


  };

  return (
    <div className="App">
      <h1>Employee Dashboard</h1>
      <div className="employee-list">
        <h2>Employee List</h2>
        <ul>
          {employees.map(employee => (
            <li key={employee.id}>
              {employee.employee_name} - {employee.employee_salary} - {employee.employee_age}
              <button onClick={() => handleEdit(employee)}>Edit</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="employee-details">
        <h2>Employee Details</h2>
        {selectedEmployee ? (
          <div>
            <p>Name: {selectedEmployee.employee_name}</p>
            <p>Salary: {selectedEmployee.employee_salary}</p>
            <p>Age: {selectedEmployee.employee_age}</p>
            <button onClick={() => handleSave(selectedEmployee)}>Save</button>
          </div>
        ) : (
          <p>Select an employee to edit</p>
        )}
      </div>
    </div>
  );
}

export default App;
