import React, { useState } from 'react';
import './App.css';

// Sample employee data
const initialEmployees = [
  { id: 1, name: 'John Doe', position: 'Software Engineer' },
  { id: 2, name: 'Jane Smith', position: 'Data Scientist' },
  { id: 3, name: 'Alex Johnson', position: 'Product Manager' },
];

function App() {
  const [employees, setEmployees] = useState(initialEmployees);
  const [selectedEmployee, setSelectedEmployee] = useState(null);

  const handleEdit = (employee) => {
    setSelectedEmployee(employee);
  };

  const handleSave = (updatedEmployee) => {
    const updatedEmployees = employees.map(emp =>
      emp.id === updatedEmployee.id ? updatedEmployee : emp
    );
    setEmployees(updatedEmployees);
    setSelectedEmployee(null);
  };

  return (
    <div className="App">
      <h1>Employee Dashboard</h1>
      <div className="employee-list">
        <h2>Employee List</h2>
        <ul>
          {employees.map(employee => (
            <li key={employee.id}>
              {employee.name} - {employee.position}
              <button onClick={() => handleEdit(employee)}>Edit</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="employee-details">
        <h2>Employee Details</h2>
        {selectedEmployee ? (
          <div>
            <p>Name: {selectedEmployee.name}</p>
            <p>Position: {selectedEmployee.position}</p>
            <button onClick={() => handleSave(selectedEmployee)}>Save</button>
          </div>
        ) : (
          <p>Select an employee to edit</p>
        )}
      </div>
    </div>
  );
}

export default App;
